import React, {useRef} from "react";
import {View, Text, StyleSheet, TouchableOpacity, FlatList, Image, Dimensions, SafeAreaView, Animated} from "react-native";

const imagenes = ["https://static.wikia.nocookie.net/thewolfofwallstreetfilm/images/3/3d/Donnie_Azoff.png/revision/latest?cb=20201024210612","https://cdn.hobbyconsolas.com/sites/navi.axelspringer.es/public/media/image/2014/01/275471-critica-lobo-wall-street.jpg?tf=3840x","https://assets.mycast.io/characters/brad-bodnick-1367620-normal.jpg?1610129690"]

const  width = Dimensions.get("window").width;
const height = Dimensions.get("window").height;


const ANCHO_CONTENEDOR = width * 0.7;
const ESPACIO = 10;

const PersonajesScreen = () => {
    return (
        <SafeAreaView style={styles.container}>
          <Animated.FlatList 
     
          data={imagenes}
          horizontal={true}
          showsHorizontalScrollIndicator={false}
          contentContainerStyle={{paddingTop: 200}}
          decelerationRate={0}
          snapToInterval={ANCHO_CONTENEDOR}
          scrollEventThrottle={16}
          keyExtractor={(item) => item}
          renderItem={({item, index}) =>{
            return (
                <View style={{width: ANCHO_CONTENEDOR}}>

<View style={{
    marginHorizontal: ESPACIO, 
    padding: ESPACIO,
    borderRadius: 34,
    backgroundColor: "#fff",
    alignItems: "center"
}}>
    <Image source={{uri:item}} style={styles.posterImage} />
</View>

                </View>
            )
          }}

          ></Animated.FlatList>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
        alignItems: "center",
        justifyContent: "center",
    },
    posterImage: {
        width: "100%",
        height: ANCHO_CONTENEDOR * 1.2,
        resizeMode: "cover",
        borderRadius: 24,
        margin: 0,
        marginBottom: 10,
    }
})

export default PersonajesScreen;
