import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import axios from 'axios';

const WeatherScreen = () => {
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    fetchWeatherData();
  }, []);

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(
        'https://api.openweathermap.org/data/2.5/weather?q=Santo%20Domingo,do&units=metric&appid=YOUR_API_KEY'
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      {weatherData ? (
        <View>
          <Text style={{ fontSize: 24 }}>Clima en República Dominicana</Text>
          <Text style={{ fontSize: 18 }}>Ciudad: {weatherData.name}</Text>
          <Text style={{ fontSize: 18 }}>Temperatura: {weatherData.main.temp}°C</Text>
          <Text style={{ fontSize: 18 }}>Descripción: {weatherData.weather[0].description}</Text>
        </View>
      ) : (
        <Text>Cargando el clima...</Text>
      )}
    </View>
  );
};

export default WeatherScreen;
